import { FormHelperText } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

export default makeStyles((theme) => ({
  mainContainer: {
    display: 'flex',
    alignItems: 'center',
  },
  smMargin: {
    margin: theme.spacing(1),
  },
  font: {
    color: 'white',
    fontWeight: 'bold',
  },
  loading: {
    display: FormHelperText,
    color: 'white'
  },
  actionDiv: {
    textAlign: 'center',
  },
}));
